    event.preventDefault();
    // console.log(event.target);
  }
  console.log(todo);
  return (
    <form onSubmit={handleFormSubmit}>
      <label>
        <input
          type="text"
          placeholder="Enter new todo"
          value={todo.task}
          //   onChange={(event) => {
          //     setTodo({ ...todo, task: event.target.value });
          //   }}
        />
      </label>
      <button type="submit">Submit</button>
    </form>
  );
}
