import { useState } from 'react';
import './App.css';
import TodoForm from './components/Todos/TodoForm';
import TodoList from './components/Todos/TodoList';

function App() {
  const [todo, setTodo] = useState(['todo one', 'todo two']);

  function handleChange(text) {
    setTodo([...todo, text]);
  }

  return (
    <div className="App">
      <h1>Todo App</h1>
      <TodoForm handle={handleChange} />
      <TodoList todo={todo} />
    </div>
  );
}

export default App;
